import React, { useState } from "react";
import Hour from "./Hour";
import Minute from "./Minute";
import Seconds from "./Seconds"

const initialState = {
  time: new Date(),
  hours: time.getHours(),
  min: time.getMinutes(),
  sec: time.getSeconds()
}

function App() {
  let [time, setTime] = useState(initialState);
  let [hours, setHours] = useState(initialState.hours);
  let [min, setMin] = useState(time.getMinutes());
  let [sec, setSec] = useState(time.getSeconds());

  console.log("APp rendered");


  const currentTime = () => {
    setTime(time = new Date());

    if(hours != time.getHours()) {
      setHours(time.getHours());
    }

    if(min != time.getMinutes()) {
      setMin(time.getMinutes());
    }

    if(sec != time.getSeconds()) {
      setSec(time.getSeconds());
    }
  }

  setInterval(currentTime, 1000);

  return (
    <div className="container">
      <Hour hours={hours} />
      <h1>:</h1>
      <Minute min={min} />
      <h1>:</h1>
      <Seconds sec={sec} />
    </div>
  );
}

export default App;
